import React, { Component, useEffect, useState } from "react";
import ReactDOM from "react-dom";
import Routes from "../../Routes/Routes";
import { NavLink, useLocation } from "react-router-dom";
import Header from "../../components/header/header";
import Campaigns from "../../components/influencer-profile/campaigns";
import TransactionHistory from "../../components/influencer-profile/transaction-history";
import Requests from "../../components/influencer-profile/requests";
import "antd/dist/antd.css";
import { Tabs } from "antd";
import { apiCall } from "../../utils/common";
import { displayLog } from "../../utils/functions";
import Loader from "../../utils/Loader";
import moment from "moment";
import { Image } from "antd";

const { TabPane } = Tabs;

function InfluencerProfile() {
  const { user_id, itemData } = useLocation().state;
  const [loading, setLoading] = useState(false);

  const [influencerList, setInfluencerList] = useState([]);
  const [withdrawRequest, setwithdrawRequestList] = useState([]);
  const [totalBal, setTotalBal] = useState("");
  const [influncersTransactionData, setviewInfluncersTransactionData] =
    useState([]);

  useEffect(() => {
    // console.log("ITEMDATA", itemData);
    setLoading(true);
    viewInfluncersCampaignDetail();
    withdrawRequestList();
    viewInfluncersTransactionData();
  }, []);

  function callback(key) {
    // console.log("key", key);
  }
  //Handle the viewInfluncersTransactionData
  const viewInfluncersTransactionData = async () => {
    let reqData = {
      id: user_id,
    };
    let response = await apiCall(
      "POST",
      "viewInfluncersTransactionData",
      reqData
    );
    // console.log("RESPONSE====", response && response.data?.record);

    if (response.code == 1) {
      if (response.data?.record.length > 0) {
        setLoading(false);

        setviewInfluncersTransactionData(response && response.data?.record);
        setTotalBal(response.data.totalBalance?.amount);
      } else {
        setLoading(false);
      }
    } else if (response.code == 0) {
      displayLog(response.code, response.message);
    }
  };

  //Handle the withdrawRequestList
  const withdrawRequestList = async () => {
    let reqData = {
      id: user_id,
    };
    let response = await apiCall("POST", "withdrawRequestList", reqData);
    // console.log("RESPONSE====", response);
    if (response.code == 1) {
      if (response.data.length > 0) {
        setLoading(false);
        setwithdrawRequestList(response.data);
      } else {
        setLoading(false);
      }
    } else if (response.code == 0) {
      displayLog(response.code, response.message);
    }
  };
  //Handle the viewInfluncersCampaignDetail
  const viewInfluncersCampaignDetail = async () => {
    let reqData = {
      id: user_id,
    };
    let response = await apiCall(
      "POST",
      "viewInfluncersCampaignDetail",
      reqData
    );
    // console.log("RESPONSE", response);
    if (response.code == 1) {
      if (response.data.length > 0) {
        setLoading(false);
        setInfluencerList(response.data);
      } else {
        setLoading(false);
      }
    } else if (response.code == 0) {
      displayLog(response.code, response.message);
    }
  };
  // console.log("influncersTransactionData", itemData);
  return (
    <div>
      <Header />
      <section className="influencerProfile">
        <div className="container">
          <ul className="d-flex breadcrumbTop">
            <li>
              <NavLink to={Routes.INFLUENCER}> Influencers </NavLink>
            </li>
            <li>{itemData?.user_name}</li>
          </ul>
          <div className="influencerProfileTop">
            <div className="user-profile">
              <div className="userPicture">
                {/* <img src="images/campaign-user.jpg" alt="" /> */}
                <Image
                  // className="img-fluid w-100"
                  src={
                    itemData?.profile_images
                      ? "https://fameonyou.s3.ap-southeast-1.amazonaws.com" +
                        itemData.profile_images[0]
                      : "data:image/png;base64,"
                  }
                  fallback="data:image/png;base64,"
                />
              </div>
              <label className="userName">{itemData?.user_name}</label>
            </div>
            <div className="total-date">
              <span>
                <strong>
                  {itemData?.total_amount ? itemData?.total_amount : "0"}
                </strong>{" "}
                Total Earned
              </span>
              <span>
                <strong>
                  {moment.unix(itemData?.created_date).format("MMM DD, yyyy")}
                </strong>{" "}
                Joined
              </span>
            </div>
          </div>

          <div className="influencerProfileTabs">
            <Tabs
              defaultActiveKey="1"
              onChange={callback}
              className="custom-tabs"
            >
              <TabPane tab="Campaigns" key="1">
                <Campaigns influencerList={influencerList} loading={loading} />
              </TabPane>
              <TabPane tab="Transaction History" key="2">
                <TransactionHistory
                  influncersTransactionData={influncersTransactionData}
                  loading={loading}
                  totalBal={totalBal}
                />
              </TabPane>
              <TabPane tab="Requests" key="3">
                <Requests
                  withdrawRequestList={withdrawRequest}
                  loading={loading}
                />
              </TabPane>
            </Tabs>
          </div>
        </div>
      </section>
    </div>
  );
}

export default InfluencerProfile;
