import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import Header from "../../components/header/header";
import { apiCall } from "../../utils/common";
import moment from "moment";
import ReactPaginate from "react-paginate";
import Loader from "../../utils/Loader";
import { Image } from "antd";

function MerchantsList() {
  const history = useHistory();
  const [loading, setLoading] = useState(true);
  const [merchantlist, setMerchantlist] = useState([]);
  const [pageno, setPageno] = useState(1);
  const [limit, setLimit] = useState(10);
  const [total, setTotal] = useState(0);
  const [searchByName, setSearchByName] = useState("");

  useEffect(() => {
    getMerchantlist();
    if (localStorage.getItem("role") == 1) {
      localStorage.removeItem("merchant_id");
      localStorage.removeItem("merchant_name");
    }
  }, [pageno]);

  const getMerchantlist = async () => {
    let req_data = {
      role: localStorage.getItem("role"),
      page_no: pageno,
      limit: limit,
    };
    if (searchByName.replace(/\s+/g, "") !== "") {
      req_data["searchByName"] = searchByName;
    }
    // console.log("SEARCH", req_data);
    let res = await apiCall("POST", "viewMerchantList", req_data);
    if (res.data.Merchant.length > 0) {
      setLoading(false);
      setMerchantlist(res.data.Merchant);
      setTotal(res.data.total);
    } else {
      setLoading(false);
    }
  };

  const goMerchantcampaign = async (id, name) => {
    localStorage.setItem("merchant_id", id);
    localStorage.setItem("merchant_name", name);
    history.push({
      pathname: "/merchant-campaigns",
      state: { merchant_id: id, merchant_name: name },
    });
  };

  const handlePageClick = async (e) => {
    setPageno(e.selected + 1);
  };
  const goMerchantProfile = (mid) => {
    history.push({
      pathname: "/view-merchant-profile",
      state: { merchant_id: mid },
    });
  };

  return (
    <div>
      <Header />
      <section className="merchantsList">
        <div className="container">
          <div className="colBg colOuterBg">
            <div className="mainTitle">
              <div className="row align-items-center">
                <div className="col-md-7">
                  <h1>Merchants</h1>
                </div>
                <div className="col-md-5">
                  <div class="search-outer">
                    <button
                      type="button"
                      class="searchBtn"
                      style={{ cursor: "default" }}
                      // onClick={() => getMerchantlist()}
                    >
                      <i class="fas fa-search"></i>
                    </button>
                    <input
                      type="search"
                      class="form-control"
                      placeholder="Search merchant"
                      value={searchByName}
                      onChange={(e) => setSearchByName(e.target.value)}
                      onKeyUp={() => getMerchantlist()}
                    />
                  </div>
                </div>
              </div>
            </div>
            {loading == true ? (
              <div>
                <Loader />
              </div>
            ) : (
              <>
                {merchantlist.length > 0 ? (
                  merchantlist.map((item, index) => (
                    <div
                      className="merchantsListLi"
                      style={{ cursor: "pointer" }}
                      key={index}
                    >
                      <ul>
                        <li>
                          <div className="row">
                            <div className="col-md-1">
                              <span
                                className="merchantImg"
                                onClick={() =>
                                  goMerchantProfile(item.merchant_id)
                                }
                              >
                                <Image
                                  className="img-fluid"
                                  src={
                                    item && item.logo
                                      ? item.logo
                                      : "data:image/png;base64,"
                                  }
                                  fallback="data:image/png;base64,"
                                />
                                {/* <img
                                    src={
                                      item && item.logo
                                        ? item.logo
                                        : "images/Defaultimage.jpeg"
                                    }
                                    onError={addDefaultSrc}
                                    alt="Merchant Logo"
                                    className="img-fluid"
                                  /> */}
                              </span>
                            </div>
                            <div
                              className="col-md-6"
                              onClick={() =>
                                goMerchantcampaign(item.merchant_id, item.name)
                              }
                            >
                              <div className="merchantBox d-md-flex">
                                <div className="merchantName">
                                  <h2>{item?.name}</h2>
                                  <p>{item?.address}</p>
                                  <p>{item?.number}</p>
                                  <p>{item?.email}</p>
                                </div>
                              </div>
                            </div>
                            <div
                              className="col-md-5 text-md-right"
                              onClick={() =>
                                goMerchantcampaign(item.merchant_id, item.name)
                              }
                            >
                              <h3>{item?.latest_campaign?.campaign_title}</h3>
                              <label>
                                Latest Campaign -{" "}
                                {moment(
                                  item?.latest_campaign?.start_date
                                ).format("MMM DD, yyyy")}
                              </label>
                            </div>
                          </div>
                        </li>
                      </ul>
                    </div>
                  ))
                ) : (
                  <div className="merchantsListLi">No Records Found</div>
                )}
              </>
            )}

            {merchantlist.length > 0 ? (
              <ReactPaginate
                pageCount={Math.ceil(total / limit)}
                onPageChange={handlePageClick}
                previousLabel={"Previous"}
                nextLabel={"Next"}
                breakLabel={"..."}
                breakClassName={"page-item"}
                breakLinkClassName={"page-link"}
                containerClassName={"pagination justify-content-end"}
                pageClassName={"page-item"}
                pageLinkClassName={"page-link"}
                previousClassName={"page-item"}
                previousLinkClassName={"page-link"}
                nextClassName={"page-item"}
                nextLinkClassName={"page-link"}
                activeClassName={"active"}
                forcePage={pageno - 1}
                style={{
                  zIndex: "1",
                  color: "#fff",
                  backgroundColor: "#0f3b4b",
                  borderColor: "#0f3b4b",
                }}
              />
            ) : null}
          </div>
        </div>
      </section>
    </div>
  );
}

export default MerchantsList;
