import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import Header from "../../components/header/header";
import { apiCall } from "../../utils/common";
import moment from "moment";
import Loader from "../../utils/Loader";
import { Image } from "antd";
import firebase from "firebase/app";
import { Alert } from "reactstrap";
import ReactPaginate from "react-paginate";
import { displayLog } from "../../utils/functions";

function NotificationList() {
  const history = useHistory();
  const [loading, setLoading] = useState(false);
  const [notificationlist, setNotificationlist] = useState([]);
  const [pageno, setPageno] = useState(1);
  const [limit, setLimit] = useState(10);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    setLoading(true);
    viewNotificationList();
    if (firebase.messaging.isSupported()) {
      firebase.messaging().onMessage(
        (payload) => {
          console.log("PAYLOAD", payload);
          viewNotificationList();
          // alertify.success(payload.notification.body);
        },
        (e) => {
          console.log(e);
        }
      );
    }

    // getCampaigns();
  }, [pageno]);

  const viewNotificationList = async () => {
    let req_data = {
      role: +localStorage.getItem("role"),
      page_no: pageno,
      limit: limit,
    };

    console.log("SEARCH", req_data);
    let res = await apiCall("POST", "viewNotificationList", req_data);
    console.log("RESSSSS", res);

    if (res.data.record.length > 0) {
      setLoading(false);
      setNotificationlist(res.data?.record);
      const notId = res.data?.record.map((item) => item.notification_id);
      const notIdString = notId.toString();

      setTotal(res.data.total);
    } else {
      setLoading(false);
    }
  };
  // //NOTIFICATION COUNT
  // const getReadNotificationCount = async () => {
  //   let req_data = {
  //     role: +localStorage.getItem("role"),
  //     // page_no: pageno,
  //     // limit: limit,
  //   };
  //   let response = await apiCall("POST", "getReadNotificationCount", req_data);
  // };

  const handlePageClick = async (e) => {
    console.log("EEE", e);
    setPageno(e.selected + 1);
    viewNotificationList();
    // getReadNotificationCount();
  };

  const handleClick = async (itemData) => {
    console.log("ITMMM", itemData);
    let reqBody = {
      role: +localStorage.getItem("role"),
      notification_id: itemData.notification_id,
    };
    let response = await apiCall("POST", "readNotifications", reqBody);
    console.log("RESSSSS======", response);
    if (response.code == 0) {
      displayLog(response.code, response.message);
    } else if (response.code == 1) {
      // getReadNotificationCount();
      if (itemData.notification_type == 3) {
        history.push({
          pathname: "/campaign",
          state: {
            merchant_id: itemData.merchant_id,
            campaign_id: itemData.campaign_id,
            campaign_title: itemData.campaign_title,
          },
        });
      } else if (itemData.notification_type == 4) {
        history.push({
          pathname: "/taskview",
          state: {
            campaign_id: itemData.campaign_id,
            user_id: itemData.sender_id,
            merchant_id: itemData.merchant_id,
            campaign_title: itemData.campaign_title,
          },
        });
      }
    }
  };
  // console.log("notificationlist", notificationlist);

  return (
    <div>
      <Header />
      <section className="merchantsList">
        <div className="container">
          <div className="colBg colOuterBg">
            <div className="mainTitle">
              <div className="row align-items-center">
                <div className="col-md-7">
                  <h1>Notifications</h1>
                </div>
              </div>
            </div>

            {loading == true ? (
              <div>
                <Loader />
              </div>
            ) : (
              <>
                {notificationlist.length > 0 ? (
                  notificationlist.map((item, index) => (
                    <div
                      className="merchantsListLi"
                      key={index}
                      style={{ cursor: "pointer" }}
                      onClick={() => handleClick(item)}
                    >
                      <ul>
                        <li>
                          <div className="row">
                            <div className="col-md-1">
                              <span className="merchantImg">
                                <Image
                                  className="img-fluid"
                                  src={
                                    item && item.profile_images
                                      ? "https://fameonyou.s3.ap-southeast-1.amazonaws.com" +
                                        item?.profile_images[0]
                                      : "data:image/png;base64,"
                                  }
                                  fallback="data:image/png;base64,"
                                />
                                {/* <img
                                    src={
                                      item && item.logo
                                        ? item.logo
                                        : "images/Defaultimage.jpeg"
                                    }
                                    onError={addDefaultSrc}
                                    alt="Merchant Logo"
                                    className="img-fluid"
                                  /> */}
                              </span>
                            </div>
                            <div className="col-md-6">
                              <div className="merchantBox d-md-flex">
                                <div className="merchantName">
                                  <h2>{item?.notification_title}</h2>
                                  <p>{item?.notification_text}</p>
                                </div>
                              </div>
                            </div>
                            <div className="col-md-5 text-md-right">
                              <label className="n-date">
                                <p>
                                  {moment
                                    .unix(item?.created_date)
                                    .format("MMM DD, yyyy")}
                                </p>
                                {item?.is_read == 0 && (
                                  <>
                                    {" "}
                                    <span class="n-read-blue"></span>{" "}
                                  </>
                                )}
                              </label>
                            </div>
                          </div>
                        </li>
                      </ul>
                    </div>
                  ))
                ) : (
                  <div className="merchantsListLi">No Records Found</div>
                )}
              </>
            )}

            <br />
            {notificationlist.length > 0 ? (
              <ReactPaginate
                pageCount={Math.ceil(total / limit)}
                onPageChange={(e) => handlePageClick(e)}
                previousLabel={"Previous"}
                nextLabel={"Next"}
                breakLabel={"..."}
                breakClassName={"page-item"}
                breakLinkClassName={"page-link"}
                containerClassName={"pagination justify-content-end"}
                pageClassName={"page-item"}
                pageLinkClassName={"page-link"}
                previousClassName={"page-item"}
                previousLinkClassName={"page-link"}
                nextClassName={"page-item"}
                nextLinkClassName={"page-link"}
                activeClassName={"active"}
                forcePage={pageno - 1}
                style={{
                  zIndex: "1",
                  color: "#fff",
                  backgroundColor: "#0f3b4b",
                  borderColor: "#0f3b4b",
                }}
              />
            ) : null}
          </div>
        </div>
      </section>
    </div>
  );
}

export default NotificationList;
